import { useAuth } from './Hooks/useAuth';
import "bootstrap/dist/css/bootstrap.min.css"
import "bootstrap/dist/js/bootstrap"

const App = () => {
  const {token} = useAuth()

  if(token){
    return <Private />
  }
  return  <Public />

}

export default App;
